class Solution:
    def beautifulNumbers(self, l: int, r: int) -> int:

        def f(x):
            digits = list(map(int, str(x)))
            n = len(digits)

            @cache
            def dp(i, is_limit, is_zero, has_zero, s, p):
                if i == n:
                    if not is_zero:
                        return 0
                    if has_zero:
                        return 1
                    if p % s == 0:
                        return 1
                    else:
                        return 0

                res = 0
                limit = digits[i] if is_limit else 9
                for d in range(limit + 1):
                    bound = is_limit and (d == limit)
                    if not is_zero:
                        if d == 0:
                            res += dp(i + 1, bound, False, False, 0, 1)
                        else:
                            res += dp(i + 1, bound, True, False, d, d)
                    else:
                        if d == 0:
                            res += dp(i + 1, bound, True, True, s + d, 0)
                        else:
                            res += dp(i + 1, bound, True, has_zero, s + d, (0 if has_zero else p * d))
                return res

            return dp(0, True, False, False, 0, 1)

        return f(r) - f(l - 1)

