class Solution:
    def beautifulNumbers(self, l: int, r: int) -> int:


        def ddp(num):

            s = str(num)
            ls = len(s)
            c0 = int(s[0])

            # isn, isl, mul, su
            dp = defaultdict(int)

            dp[(1, 1, c0, c0)] = 1

            for i in range(1, c0):
                dp[(1, 0, i, i)] += 1

            dp[(0, 0, 0, 0)] = 1

            for i in range(1, ls):
                c0 = int(s[i])
                tmp = defaultdict(int)
                tmp[(0, 0, 0, 0)] = 1
                for (isn, isl, mul, su), val in dp.items():

                    if isn == 0:
                        for j in range(1, 10):
                            tmp[(1, 0, j, j)] += 1

                    elif isl == 0:
                        for j in range(10):
                            tmp[(1, 0, j * mul, j + su)] += val

                    else:
                        for j in range(c0):
                            tmp[(1, 0, j * mul, j + su)] += val
                        tmp[(1, 1, c0 * mul, c0 + su)] += val
                dp = tmp


            ans = 0
            for (isn, isl, mul, su), val in dp.items():
                if isn == 1 and mul % su == 0:
                    ans += val

            return ans


        hi = ddp(r)
        lo = ddp(l)

        sums = 0
        mult = 1
        res = 0

        for c in str(l):
            sums += int(c)
            mult *= int(c)

        if mult % sums == 0:
            res += 1

        return res + hi - lo

