class Solution:
    def beautifulNumbers(self, l: int, r: int) -> int:
        def count_beautiful(n):
            digits = list(map(int, str(n)))

            # Memoization dictionary
            memo = {}

            def dp(pos, sum_digits, product_digits, tight, started):
                if pos == len(digits):  # Base case: all digits processed
                    return 1 if started and sum_digits != 0 and product_digits % sum_digits == 0 else 0

                # Check memoization table
                key = (pos, sum_digits, product_digits, tight, started)
                if key in memo:
                    return memo[key]

                limit = digits[pos] if tight else 9
                count = 0

                for digit in range(0, limit + 1):
                    new_product = product_digits * digit if started else digit  # Avoid multiplying by zero initially
                    new_sum = sum_digits + digit
                    count += dp(
                        pos + 1,
                        new_sum,
                        new_product if started or digit > 0 else 1,  # Prevent product from being stuck at 0
                        tight and (digit == limit),
                        started or digit > 0  # Ensure leading zeroes are handled correctly
                    )

                # Store the result in memoization table
                memo[key] = count
                return count

            return dp(0, 0, 1, True, False)

        return count_beautiful(r) - count_beautiful(l - 1)

