static int dp[10][82][82][2][2];

class Solution {
public:
    int n, mod;
    string s;
    int beautifulNumbers(int l, int r) {
        int ans = 0;
        for(mod = 1; mod < 82; ++mod) {
            s = to_string(l - 1);
            n = s.length();
            memset(dp, -1, sizeof(dp));
            int lcnt = dfs(0, 1, 0, 1, 0);
            s = to_string(r);
            n = s.length();
            memset(dp, -1, sizeof(dp));
            int rcnt = dfs(0, 1, 0, 1, 0);
            ans += rcnt - lcnt;
        }
        return ans;
    }

    int dfs(int i, int mul, int su, int islimit, int isnum) {
        if(su > mod) {
            return 0;
        }
        if(i == n) {
            return dp[i][mul][su][islimit][isnum] = (mul == 0 && mod == su && isnum == 1) ? 1 : 0;
        }
        int& v = dp[i][mul][su][islimit][isnum];
        if(v != -1) return v;
        int cnt = 0;
        if(!isnum) {
            cnt += dfs(i + 1, mul, su, 0, 0);
        }
        int digit = s[i] - '0';
        int ub = islimit ? digit : 9;
        for(int d = 1 - isnum; d <= ub; ++d) {
            cnt += dfs(i + 1, (mul * d) % mod, su + d, islimit && d == digit, 1);
        }
        return v = cnt;
    }
};

