class Solution {
public:
    using ll = long long;
    string s;
    unordered_map<string, ll> memo;

    ll dp(int pos, bool tight, bool started, bool hasZero, int sum, ll prod) {
        if (pos == s.size()) {
            if (!started) return 0;
            if (hasZero) return 1;
            return (prod % sum == 0) ? 1 : 0;
        }
        string key = to_string(pos) + "|" + to_string(tight) + "|" + to_string(started) 
                     + "|" + to_string(hasZero) + "|" + to_string(sum) + "|" + to_string(prod);
        if(memo.count(key))
            return memo[key];
        ll res = 0;
        int limit = tight ? s[pos] - '0' : 9;
        for (int d = 0; d <= limit; d++) {
            bool newTight = (tight && (d == limit)), newStarted = started || (d != 0), newHasZero = hasZero;
            int newSum = sum;
            ll newProd = prod;
            if(!(!started && d == 0)) {
                if (d == 0) {
                    newHasZero = true;
                    newProd = 0;
                } else {
                    newSum += d;
                    newProd *= d;
                }
            }
            res += dp(pos + 1, newTight, newStarted, newHasZero, newSum, newProd);
        }
        memo[key] = res;
        return res;
    }
    ll countBeautiful(ll x) {
        s = to_string(x);
        memo.clear();
        return dp(0, true, false, false, 0, 1);
    }
    int beautifulNumbers(int l, int r) {
        return countBeautiful(r) - countBeautiful(l - 1);
    }
};

