class Solution:
    def beautifulNumbers(self, l: int, r: int) -> int:
        from functools import lru_cache
        def get_digits(n: int):
            return list(map(int, str(n)))

        @lru_cache(maxsize=None)
        def dp(pos, tight, digit_sum, digit_product, leading_zero, digits):
            if pos == len(digits):
                return 1 if digit_sum != 0 and digit_product % digit_sum == 0 else 0

            limit = digits[pos] if tight else 9
            result = 0

            for d in range(0, limit + 1):
                new_sum = digit_sum + d
                new_product = digit_product * d if not leading_zero or d > 0 else 1
                result += dp(pos + 1, tight and (d == limit), new_sum, new_product, leading_zero and d == 0, digits)

            return result

        def count_beautiful(n):
            digits = get_digits(n)
            return dp(0, True, 0, 1, True, tuple(digits))

        return count_beautiful(r) - count_beautiful(l - 1)

