class Solution {

    public int beautifulNumbers(int l, int r) {
        return solve(r + 1) - solve(l);
    }

    public int solve(int x) {
        return get("" + x, 0, false, false, 0, 1, false, new HashMap<>());
    }

    public int get(String s, int i, boolean started, boolean less, int sum, int prod, boolean stop, HashMap<String, Integer> dp) {
        String temp = i + " " + started + " " + less + " " + sum + " " + prod + " " + stop;
        if (dp.containsKey(temp)) {
            return dp.get(temp);
        }
        if (less && stop) {
            return (int) Math.pow(10, s.length() - i);
        }
        if (i == s.length()) {
            if (!started || !less) {
                return 0;
            }
            if (prod % sum == 0) return 1;
            return 0;
        }
        int up = 0;
        if (less) up = 9;
        else up = s.charAt(i) - '0';
        int ans = 0;
        for (int j = 0; j <= up; j++) {
            boolean l = j < up || less;
            if (!started && j == 0) {
                ans += get(s, i + 1, false, l, 0, 1, stop, dp);
            }
            else if (!started) {
                ans += get(s, i + 1, true, l, j, j, stop, dp);
            }
            if (started) {
                if (j == 0) {
                    ans += get(s, i + 1, false, l, sum, 0, true, dp);
                }
                else {
                    ans += get(s, i + 1, true, l, sum + j, prod * j, stop, dp);
                }
            }
        }
        dp.put(temp, ans);
        return ans;
    }
}

