class Solution:
    def beautifulNumbers(self, l: int, r: int) -> int:
        count_r = self.count_up_to(r)
        count_l_minus = self.count_up_to(l - 1)
        return count_r - count_l_minus
    def count_up_to(self, n: int) -> int:
        if n < 1:
            return 0
        digits = list(str(n))
        memo = {}
        return self.dp(0, True, False, 0, 1, digits, memo)
    def dp(self, pos: int, tight: bool, started: bool, sum_: int, prod: int, digits: list, memo: dict) -> int:
        if pos == len(digits):
            if started and sum_ > 0 and prod % sum_ == 0:
                return 1
            return 0
        key = (pos, tight, started, sum_, prod)
        if key in memo:
            return memo[key]
        limit = int(digits[pos]) if tight else 9
        res = 0
        for d in range(0, limit + 1):
            next_tight = tight and (d == limit)
            new_started = started or (d != 0)
            new_sum = sum_
            new_prod = prod
            if new_started:
                if not started:
                    new_sum = d
                    new_prod = d
                else:
                    new_sum = sum_ + d
                    new_prod = prod * d
            res += self.dp(pos + 1, next_tight, new_started, new_sum, new_prod, digits, memo)
        memo[key] = res
        return res

