class Solution {
       Map<String, Long> memo;
    String s; // String representation of current upper bound

    public int beautifulNumbers(int l, int r) {
        long countR = countBeautiful(r);
        long countL = countBeautiful(l - 1);
        return (int)(countR - countL);
    }

    // Counts beautiful numbers  num using digit DP
    private long countBeautiful(int num) {
        if (num < 0) return 0; // No numbers below 0
        s = String.valueOf(num);
        memo = new HashMap<>();
        // Start at position 0, tight = 1 (following s), not started, sum=0, product=1
        return dfs(0, 1, false, 0, 1);
    }   
    private long dfs(int pos, int tight, boolean started, int sum, int prod) {
        if (pos == s.length()) {
            return (started && sum > 0 && prod % sum == 0) ? 1 : 0;
        }

        // Memoization key
        String key = pos + "," + tight + "," + started + "," + sum + "," + prod;
        if (memo.containsKey(key)) {
            return memo.get(key);
        }

        int limit = (tight == 1) ? s.charAt(pos) - '0' : 9;
        long res = 0;

        // Try every digit from 0 to limit
        for (int dig = 0; dig <= limit; dig++) {
            int ntight = (tight == 1 && dig == limit) ? 1 : 0;
            boolean nstarted = started || (dig != 0);
            int nsum = sum;
            int nprod = prod;

            if (nstarted) {
                if (!started && dig != 0) { // Starting number now
                    nsum = dig;
                    nprod = dig;
                } else if (started) {
                    nsum = sum + dig;
                    nprod = prod * dig;
                }
            }

            res += dfs(pos + 1, ntight, nstarted, nsum, nprod);
        }

        memo.put(key, res);
        return res;
    }
}

