#include <bits/stdc++.h>
using namespace std;

class Solution {
public:
    int beautifulNumbers(int l, int r) {
        return countBeautiful(r) - countBeautiful(l - 1);
    }

private:
    map<tuple<int, int, int, int>, int> dp;

    int countBeautiful(int num) {
        if (num == 0) return 0;
        vector<int> digits;
        while (num > 0) {
            digits.push_back(num % 10);
            num /= 10;
        }
        reverse(digits.begin(), digits.end());
        dp.clear();
        return digitDP(0, 1, 0, 1, digits);
    }

    int digitDP(int pos, int tight, int sum, int prod, const vector<int>& digits) {
        if (pos == digits.size())  
            return (sum > 0 && prod % sum == 0) ? 1 : 0;

        auto state = make_tuple(pos, tight, sum, prod);
        if (dp.count(state))  
            return dp[state];

        int limit = tight ? digits[pos] : 9, count = 0;
        for (int d = 0; d <= limit; d++) {
            int newProd = (sum == 0 && d == 0) ? 1 : prod * d;
            count += digitDP(pos + 1, (tight && d == limit) ? 1 : 0, sum + d, newProd, digits);
        }

        return dp[state] = count;
    }
};

