class Solution:
    def beautifulNumbers(self, l: int, r: int) -> int:
        def count_beautiful(n: int) -> int:
            s = str(n)
            memo = {}

            def dp(pos: int, tight: bool, started: bool, sum_d: int, prod: int, has_zero: bool) -> int:
                # Base-case: if we have processed all digits:
                if pos == len(s):
                    if not started:
                        return 0  # no number was formed
                    # If a zero has been encountered, product = 0 and 0 % sum is 0.
                    if has_zero:
                        return 1
                    # Otherwise, check divisibility: (prod % sum_d == 0)
                    return 1 if sum_d > 0 and prod % sum_d == 0 else 0

                # Memoization key for current state
                key = (pos, tight, started, sum_d, prod, has_zero)
                if key in memo:
                    return memo[key]

                res = 0
                # Determine the maximum digit we can use in this position
                end_digit = int(s[pos]) if tight else 9

                for d in range(0, end_digit + 1):
                    ntight = tight and (d == end_digit)
                    if not started and d == 0:
                        # Still haven't started the number; sum and product remain as initial values.
                        res += dp(pos + 1, ntight, False, 0, 1, False)
                    else:
                        # We are now starting (or have already started) the number.
                        nstarted = True
                        nsum = sum_d + d
                        # If we have already encountered a zero or the current digit is 0, mark has_zero as True.
                        if has_zero or d == 0:
                            nhas_zero = True
                            nprod = 0  # Once a zero appears, product is 0.
                        else:
                            nhas_zero = False
                            nprod = prod * d
                        res += dp(pos + 1, ntight, nstarted, nsum, nprod, nhas_zero)

                memo[key] = res
                return res

            return dp(0, True, False, 0, 1, False)
        if (l <= 1):
            return count_beautiful(r)
        else:
            return count_beautiful(r) - count_beautiful(l - 1)

