class Solution:
    def beautifulNumbers(self, l: int, r: int) -> int:

        def f(x):
            S = str(x)
            n = len(S)
            res = 0

            @cache
            def dp(n, m, s):
                if n <= 0:
                    return 1 if s != 0 and m % s == 0 else 0
                cur = 0
                for i in range(10):
                    cur += dp(n-1, m*i, s+i)
                return cur

            for l in range(1, n):
                for x in range(1, 10):
                    res += dp(l-1, x, x)

            @cache
            def g(index, first, top, m, s):
                if index == n:
                    return 1 if s != 0 and m % s == 0 else 0
                x = int(S[index])
                up = x if top else 10
                down = 1 if first else 0
                cur = 0
                for i in range(down, up):
                    cur += dp(n-index-1, m*i, s+i)
                if top:
                    cur += g(index+1, False, True, m*x, s+x)
                return cur

            res += g(0, True, True, 1, 0)
            # print(x, res)
            return res

        return f(r) - f(l-1)

