class Solution:
    def beautifulNumbers(self, l: int, r: int) -> int:
        def count_no_zero(n):
            s = str(n)
            L = len(s)

            @cache
            def dp(pos, tight, started):
                if pos == L:
                    return 1 if started else 0
                res = 0
                limit = int(s[pos]) if tight else 9
                for d in range(0, limit + 1):
                    ntight = tight and (d == limit)
                    if not started:
                        if d == 0:
                            res += dp(pos+1, ntight, False)
                        else:
                            res += dp(pos+1, ntight, True)
                    else:
                        if d == 0:
                            continue
                        res += dp(pos+1, ntight, True)
                return res

            return dp(0, True, False)

        def count_beautiful_no_zero(n):
            s = str(n)
            L = len(s)

            @cache
            def dp(pos, tight, started, s_val, p_val):
                if pos == L:
                    if started:
                        return 1 if (p_val % s_val == 0) else 0
                    return 0
                res = 0
                limit = int(s[pos]) if tight else 9
                for d in range(0, limit + 1):
                    ntight = tight and (d == limit)
                    if not started:
                        if d == 0:
                            res += dp(pos+1, ntight, False, s_val, p_val)
                        else:
                            res += dp(pos+1, ntight, True, s_val + d, p_val * d)
                    else:
                        if d == 0:
                            continue
                        res += dp(pos+1, ntight, True, s_val + d, p_val * d)
                return res

            return dp(0, True, False, 0, 1)

        def f(x):
            if x < 1:
                return 0
            return count_beautiful_no_zero(x) + (x - count_no_zero(x))
        return f(r) - f(l - 1)

