class Solution {
    string hash(string &str, int i, int upto, int done, int zero, int p, int s){
         string key = to_string(i) + "*" + to_string(upto) + "_" + to_string(done) + "?" + to_string(zero) + "/" + to_string(p) + "]" + to_string(s);
        return key;
    }
    int fn2(unordered_map<string, int> &dp, string &str, int i, int upto, int done, int zero, int p, int s){
        if(i == str.size()){
            if(!done) return 0;
            if(zero) return 1;
            if(p%s==0) return 1;
             return 0;
        }

        string key = hash(str,i,upto,done,zero,p,s);
        if(dp.find(key)!=dp.end()) return dp[key];
        int ans = 0, lim = 9;
        if(upto) lim = str[i]-'0';
        for(int d = 0; d <= lim; d++){
            if(!done){
                if(d == 0)  ans += fn2(dp,str,i + 1, (upto && (d==lim)), 0, 0, 1, 0);
                else     ans += fn2(dp,str,i + 1, (upto && (d==lim)), 1, 0, d, d);
            }        
            else{
                if(zero) ans += fn2(dp,str,i + 1, (upto && (d==lim)), 1, 1, 0, 0);
                else{
                    if(d == 0) ans += fn2(dp,str,i + 1, (upto && (d==lim)), 1, 1, 0, 0);
                    else ans += fn2(dp,str,i + 1, (upto && (d==lim)), 1, 0, p*d , s+d);
                }
            }
        }
        return dp[key] = ans;
    }
    int fn(int n) {
        if(n == 0) return 0;
        string s = to_string(n);
        unordered_map<string, int> dp;
        return fn2(dp,s, 0, 1, 0, 0, 0, 1);
    }
public:
    int beautifulNumbers(int l, int r) {
        return fn(r) - fn(l-1);
    }
};

