#define all(x) (x).begin(), (x).end()

template<class T> inline bool chmax(T &a, T b){if (a < b) {a = b;return 1;}return 0;}
template<class T> inline bool chmin(T &a, T b){if (a > b) {a = b;return 1;}return 0;}

// https://atcoder.jp/contests/abc336/tasks/abc336_e

struct State {
    int pos, tight, started, sum;
    ll prod;
    bool operator<(const State &other) const {
        if (pos != other.pos) return pos < other.pos;
        if (tight != other.tight) return tight < other.tight;
        if (started != other.started) return started < other.started;
        if (sum != other.sum) return sum < other.sum;
        return prod < other.prod;
    }
};

ll dp(int pos, int tight, int start, int sum, ll prod, string &s, map<State, ll> &mp){
    if (pos == s.size()){
        if (!start) return (ll)0;
        if (sum > 0 && (prod%sum == 0)) return (ll)1;
        else return (ll)0;
    }

    State st{pos, tight, start, sum, prod};
    if (mp.find(st) != mp.end()) return mp[st];

    ll ways = 0;
    int lim = tight ? s[pos]-'0':9;
    rep(d, lim+1){
        int ntight = (tight && (d == lim)) ? 1 : 0;
        int nstart = start; 
        int nsum = sum;
        ll nprod = prod;

        if (!start){
            if (d == 0)
                ways += dp(pos+1, ntight, 0, 0, 1, s, mp);
            else{
                nstart = 1;
                nsum = d;
                nprod = d;
                ways += dp(pos+1, ntight, 1, nsum, nprod, s, mp);
            }
        }
        else {
            nsum = sum+d;
            nprod = prod*d;
            ways += dp(pos+1, ntight, 1, nsum, nprod, s, mp);
        }
    }
    return mp[st] = ways;
}

ll solve(ll x){
    if (x < 1) return 0;
    string s = to_string(x);
    map<State, ll> mp;
    return dp(0, 1, 0, 0, 1, s, mp);
}

class Solution {
public:
    int beautifulNumbers(int l, int r) {
        ll ans = solve(r) - solve(l-1);
        return ans;
    }
};

