import java.util.*;

class Solution {

    public int beautifulNumbers(int l, int r) {

        int countZeros = countZerosUpto(r) - countZerosUpto(l - 1);

        int countNonZero = countNonZeroBeautifulDP(r) - countNonZeroBeautifulDP(l - 1);
        return countZeros + countNonZero;
    }


    private int countZerosUpto(int x) {
        if (x < 1) return 0;
        char[] s = Integer.toString(x).toCharArray();
        int n = s.length;
        Integer[][][][] memoZeros = new Integer[n][2][2][2];
        return dfsZeros(s, 0, 1, 0, 0, memoZeros);
    }

    private int dfsZeros(char[] s, int pos, int tight, int started, int hasZero, Integer[][][][] memo) {
        if (pos == s.length) {
            return (started == 1 && hasZero == 1) ? 1 : 0;
        }
        if (memo[pos][tight][started][hasZero] != null) {
            return memo[pos][tight][started][hasZero];
        }
        int limit = (tight == 1 ? s[pos] - '0' : 9);
        int count = 0;
        for (int d = 0; d <= limit; d++) {
            int newTight = (tight == 1 && d == limit) ? 1 : 0;
            int newStarted = started;
            int newHasZero = hasZero;
            if (started == 0) {
                if (d != 0) {
                    newStarted = 1;
                }
            }
            if (newStarted == 1) {
                if (d == 0) {
                    newHasZero = 1;
                }
            }
            count += dfsZeros(s, pos + 1, newTight, newStarted, newHasZero, memo);
        }
        memo[pos][tight][started][hasZero] = count;
        return count;
    }


    private Map<DPKey, Integer> memo;

    private int countNonZeroBeautifulDP(int x) {
        String s = Integer.toString(x);
        int n = s.length();
        int[] digits = new int[n];
        for (int i = 0; i < n; i++) {
            digits[i] = s.charAt(i) - '0';
        }
        memo = new HashMap<>();

        return dpNonZero(digits, 0, 1, false, 0, 0, 0, 0, 0);
    }


    private int dpNonZero(int[] digits, int pos, int tight, boolean started,
                          int sum, int f2, int f3, int f5, int f7) {
        if (pos == digits.length) {
            if (!started) return 0; 
            if (sum == 0) return 0;
            int[] req = factorize(sum);

            if (req[3] == -1) return 0;
            if (f2 >= req[0] && f3 >= req[1] && f5 >= req[2] && f7 >= req[3]) return 1;
            return 0;
        }
        DPKey key = new DPKey(pos, tight, started, sum, f2, f3, f5, f7);
        if (memo.containsKey(key)) return memo.get(key);
        int res = 0;
        int limit = (tight == 1 ? digits[pos] : 9);
        for (int d = 0; d <= limit; d++) {
            int ntight = (tight == 1 && d == limit) ? 1 : 0;
            if (!started) {
                if (d == 0) {

                    res += dpNonZero(digits, pos + 1, ntight, false, 0, 0, 0, 0, 0);
                } else {

                    int[] factors = digitFactors(d);
                    res += dpNonZero(digits, pos + 1, ntight, true, d,
                                     factors[0], factors[1], factors[2], factors[3]);
                }
            } else {

                if (d == 0) continue;
                int[] factors = digitFactors(d);
                res += dpNonZero(digits, pos + 1, ntight, true, sum + d,
                                 f2 + factors[0], f3 + factors[1],
                                 f5 + factors[2], f7 + factors[3]);
            }
        }
        memo.put(key, res);
        return res;
    }


    private int[] digitFactors(int d) {
        switch(d) {
            case 1: return new int[]{0, 0, 0, 0};
            case 2: return new int[]{1, 0, 0, 0};
            case 3: return new int[]{0, 1, 0, 0};
            case 4: return new int[]{2, 0, 0, 0};
            case 5: return new int[]{0, 0, 1, 0};
            case 6: return new int[]{1, 1, 0, 0};
            case 7: return new int[]{0, 0, 0, 1};
            case 8: return new int[]{3, 0, 0, 0};
            case 9: return new int[]{0, 2, 0, 0};
        }
        return new int[]{0, 0, 0, 0}; 
    }


    private int[] factorize(int s) {
        int[] res = new int[4]; // for 2,3,5,7
        int temp = s;
        while (temp % 2 == 0) { res[0]++; temp /= 2; }
        while (temp % 3 == 0) { res[1]++; temp /= 3; }
        while (temp % 5 == 0) { res[2]++; temp /= 5; }
        while (temp % 7 == 0) { res[3]++; temp /= 7; }
        if (temp != 1) { 

            res[3] = -1; 
        }
        return res;
    }


    class DPKey {
        int pos, tight, sum, f2, f3, f5, f7;
        boolean started;

        public DPKey(int pos, int tight, boolean started, int sum, int f2, int f3, int f5, int f7) {
            this.pos = pos;
            this.tight = tight;
            this.started = started;
            this.sum = sum;
            this.f2 = f2;
            this.f3 = f3;
            this.f5 = f5;
            this.f7 = f7;
        }

        @Override
        public boolean equals(Object o) {
            if (this == o) return true;
            if (!(o instanceof DPKey)) return false;
            DPKey other = (DPKey) o;
            return pos == other.pos && tight == other.tight && started == other.started &&
                   sum == other.sum && f2 == other.f2 && f3 == other.f3 &&
                   f5 == other.f5 && f7 == other.f7;
        }

        @Override
        public int hashCode() {
            int hash = 7;
            hash = hash * 31 + pos;
            hash = hash * 31 + tight;
            hash = hash * 31 + (started ? 1 : 0);
            hash = hash * 31 + sum;
            hash = hash * 31 + f2;
            hash = hash * 31 + f3;
            hash = hash * 31 + f5;
            hash = hash * 31 + f7;
            return hash;
        }
    }
}

