        }
        return bst;
    }

    int beautifulNumbers(int l, int r) {
        int ans = 0;
        string R = to_string(r);
        string L = to_string(l-1);
        for(int i=1; i<=81; i++){
            memset(dp,-1,sizeof(dp));
            ans += solve(0,i,1,1,1,R,i);
            // cout << ans << 'n';
            memset(dp,-1,sizeof(dp));
            ans -= solve(0,i,1,1,1,L,i);
            // cout << ans << 'n';
            // cout << "foin";
        }
        return ans;
    }
};

