class Solution {
	// mul[a][b][r] = (a * b) % r

	void init() {
		FOR(i, MAXXX) {
			FOR(j, MAXXX) {
				FOR1(k, MAXXX - 1) {
					mul[i][j][k] = (i * j) % k;
				}
			}
		}
	}

	int solve(LL x) {
		if (x == 0) return 0;

		string s = to_string(x);
		int n = s.length(), i, j, k, ans, MAXX = n * 9 + 1;

		// dp[step][breached][sum][r][% r] = ct
		//vector<vector<VVVI>> dp(n + 1, vector<VVVI>(2, VVVI(MAXX, VVI(MAXX, VI(MAXX, 0)))));

		FOR(i, n + 1) FOR(j, 2) FOR(k, MAXX) FOR(l, MAXX) FOR(kk, MAXX) dp[i][j][k][l][kk] = 0;

		FOR1(i, MAXX - 1) dp[0][0][0][i][0] = 1;

		FOR(i, n) {
			LL d = s[i] - '0';
			FOR(j, 2) {
				FOR(sum, MAXX) {
					FOR1(r, MAXX - 1) {
						int maxx = 9;
						if (j == 0) maxx = d;

						FOR(rr, r) {
							if (dp[i][j][sum][r][rr] == 0) continue;
							for (int dd = 0; dd <= maxx; dd++) {
								int breached = j || (dd < d);
								int sum2 = sum + dd;
								int rrr;

								if ((sum == 0) && (rr != 0)) continue;
								//if (sum == 0) rrr = dd % r;
								//else rrr = (rr * dd) % r;
								if (sum == 0) rrr = mul[1][dd][r];
								else rrr = mul[rr][dd][r]; 

								dp[i + 1][breached][sum2][r][rrr] += dp[i][j][sum][r][rr];
							}
						}

					}
				}
			}
		}
		ans = 0;
		FOR1(i, MAXX - 1) ans += dp[n][0][i][i][0] + dp[n][1][i][i][0];

		return ans;
	}

public:
	int beautifulNumbers(int l, int r) {
		if (!used) {
			used = true;
			init();
		}
		int ll = solve(l - 1), rr = solve(r), ans = rr - ll;
		return ans;
	}
};

