from functools import lru_cache

class Solution:
    def beautifulNumbers(self, l: int, r: int) -> int:
        @lru_cache(None)
        def dp(x):
            if x == 0:
                return {}
            if x < 10:
                return {(i, i): 1 for i in range(1, x + 1)}  # prod, sum

            output = {(0, 1): x // 10}
            for i in range(1, 10):
                output[(i, i)] = 1

            for r in range(1, 10):
                if r <= x % 10:
                    _o = dp(x // 10)

                    for (p, s), f in _o.items():
                        if (p, s) == (0, 1):
                            output[(0, 1)] += f
                        else:
                            np, ns = p * r, s + r
                            output[(np, ns)] = output.get((np, ns), 0) + f
                else:
                    _o = dp(x // 10 - 1)

                    for (p, s), f in _o.items():
                        if (p, s) == (0, 1):
                            output[(0, 1)] += f
                        else:
                            np, ns = p * r, s + r
                            output[(np, ns)] = output.get((np, ns), 0) + f

            return output

        answer = 0
        r_output = dp(r)
        l_output = dp(l - 1)

        # print(r_output, l_output)

        for (p, s), f in r_output.items():
            if p % s == 0:
                answer += f

        for (p, s), f in l_output.items():
            if p % s == 0:
                answer -= f

        return answer

