import functools


class Solution:
    def beautifulNumbers(self, l: int, r: int) -> int:
        def count(x):
            if x == 0:
                return 0
            a = [int(c) for c in str(x)]
            n = len(a)

            @functools.lru_cache(None)
            def dp(i, s, m, is_less, is_leading_zero):
                if i == n:
                    return 1 if s > 0 and m % s == 0 else 0
                ans = 0
                for d in range(10):
                    next_leading_zero = is_leading_zero
                    if d > 0:
                        next_leading_zero = False
                    if d == a[i]:
                        ans += dp(i + 1, s + d, 1 if next_leading_zero else m * d, is_less, next_leading_zero)
                    elif d < a[i] or is_less:
                        ans += dp(i + 1, s + d, 1 if next_leading_zero else m * d, True, next_leading_zero)
                return ans

            res = dp(0, 0, 1, False, True)
            return res

        return count(r) - count(l - 1)

