#include <bits/stdc++.h>
using namespace std;

struct State {
    int tight, started, hasZero, sum;
    long long prod;
    bool operator==(const State &other) const {
        return tight == other.tight && started == other.started && hasZero == other.hasZero && sum == other.sum && prod == other.prod;
    }
};

struct StateHash {
    size_t operator()(const State &st) const {
        unsigned long long key = 0;
        key |= (unsigned long long) st.tight;
        key = (key << 1) | st.started;
        key = (key << 1) | st.hasZero;
        key = (key << 7) | (unsigned long long) st.sum;
        key = (key << 32) | ((unsigned long long) st.prod);
        return std::hash<unsigned long long>()(key);
    }
};

class Solution {
public:
    long long f(long long x) {
        if(x < 1) return 0;
        string s = to_string(x);
        int n = s.size();
        vector<int> digits(n);
        for (int i = 0; i < n; i++){
            digits[i] = s[i] - '0';
        }
        unordered_map<State, long long, StateHash> dp;
        State init = {1, 0, 0, 0, 1};
        dp[init] = 1;
        for (int pos = 0; pos < n; pos++){
            unordered_map<State, long long, StateHash> ndp;
            int lim;
            for (auto &item : dp) {
                State st = item.first;
                long long cnt = item.second;
                lim = (st.tight == 1 ? digits[pos] : 9);
                for (int d = 0; d <= lim; d++){
                    State nst;
                    nst.tight = (st.tight == 1 && d == lim) ? 1 : 0;
                    if (st.started == 0 && d == 0) {
                        nst.started = 0;
                        nst.sum = 0;
                        nst.prod = 1;
                        nst.hasZero = 0;
                    } else {
                        nst.started = 1;
                        if (st.started == 0) {
                            nst.sum = d;
                            nst.prod = d;
                            nst.hasZero = (d == 0) ? 1 : 0;
                        } else {
                            nst.sum = st.sum + d;
                            if (st.hasZero == 1 || d == 0) {
                                nst.hasZero = 1;
                                nst.prod = 0;
                            } else {
                                nst.hasZero = 0;
                                nst.prod = st.prod * d;
                            }
                        }
                    }
                    ndp[nst] += cnt;
                }
            }
            dp = move(ndp);
        }
        long long ans = 0;
        for (auto &item : dp) {
            State st = item.first;
            long long cnt = item.second;
            if (st.started == 1) {
                if (st.hasZero == 1) ans += cnt;
                else if (st.sum != 0 && st.prod % st.sum == 0) ans += cnt;
            }
        }
        return ans;
    }

    int beautifulNumbers(int l, int r) {
        long long res = f(r) - f(l - 1);
        return (int)res;
    }
};

