class Solution:
    def beautifulNumbers(self, l: int, r: int) -> int:
        def countBeautiful(n): # helper
            if n == 0: return 0
            digits = list(map(int, str(n)))
            @lru_cache(maxsize=None)
            def dp(i, matching, started, contains0, s, p):
                if i == len(digits):
                    if not started: return 0
                    if contains0: return 1
                    return 1 if (s != 0 and p % s == 0) else 0
                res = 0
                maxDigit = digits[i] if matching else 9
                for d in range(maxDigit+1):
                    matchingNew = matching and (d == maxDigit)
                    if not started and d == 0:
                        res += dp(i+1, matchingNew, False, False, 0, 1)
                    else:
                        contains0new = contains0 or (d == 0)
                        res += dp(i+1, matchingNew, True, contains0new, s+d, p*d)
                return res

            return dp(0, True, False, False, 0, 1)
        return countBeautiful(r)-countBeautiful(l-1)

