class Solution {
public:
    int f[82][7][5][3][3][2][2], g[82][7][5][3][3][2][2], p[15], dp[7][5][3][3];
    int mx[5] = {0,6,4,2,2};
    int solve(int x){
        vector<int> digit;
        while(x>0){digit.push_back(x%10);x/=10;}
        if(digit.empty()) digit.push_back(0);
        reverse(digit.begin(),digit.end());
        int len=digit.size();
        for(int j=0;j<=81;j++)
            for(int a=0;a<=mx[1];a++)
                for(int b=0;b<=mx[2];b++)
                    for(int c=0;c<=mx[3];c++)
                        for(int d=0;d<=mx[4];d++)
                            for(int e=0;e<=1;e++)
                                for(int o=0;o<=1;o++){
                                    f[j][a][b][c][d][e][o]=0;
                                    g[j][a][b][c][d][e][o]=0;
                                }
        f[0][0][0][0][0][0][0]=1;
        for(int i=1;i<=len;i++){
            int up=digit[i-1];
            for(int j=0;j<=(i-1)*9;j++)
                for(int a=0;a<=mx[1];a++)
                    for(int b=0;b<=mx[2];b++)
                        for(int c=0;c<=mx[3];c++)
                            for(int d=0;d<=mx[4];d++)
                                for(int e=0;e<=1;e++)
                                    for(int o=0;o<=1;o++){
                                        g[j][a][b][c][d][e][o]=f[j][a][b][c][d][e][o];
                                        f[j][a][b][c][d][e][o]=0;
                                    }
            for(int j=0;j<=(i-1)*9;j++)
                for(int a=0;a<=mx[1];a++)
                    for(int b=0;b<=mx[2];b++)
                        for(int c=0;c<=mx[3];c++)
                            for(int d=0;d<=mx[4];d++)
                                for(int e=0;e<=1;e++)
                                    for(int o=0;o<=1;o++){
                                        int base=g[j][a][b][c][d][e][o];
                                        if(!base)continue;
                                        for(int now=0;now<=9;now++){
                                            if(!e&&now>up)break;
                                            int nj=j+now,na=a,nb=b,nc=c,nd=d,ne=e,no=o;
                                            if(now<up) ne=1;
                                            if(now==0&&j!=0) no=1;
                                            if(!no){
                                                if(now==2) na++;
                                                else if(now==3) nb++;
                                                else if(now==4) na+=2;
                                                else if(now==5) nc++;
                                                else if(now==6){na++;nb++;}
                                                else if(now==7) nd++;
                                                else if(now==8) na+=3;
                                                else if(now==9) nb+=2;
                                                na=min(na,mx[1]);
                                                nb=min(nb,mx[2]);
                                                nc=min(nc,mx[3]);
                                                nd=min(nd,mx[4]);
                                            }
                                            f[nj][na][nb][nc][nd][ne][no]+=base;
                                        }
                                    }
        }
        long long ans=0;
        for(int j=0;j<=81;j++)
            for(int a=0;a<=6;a++)
                for(int b=0;b<=4;b++)
                    for(int c=0;c<=2;c++)
                        for(int d=0;d<=2;d++)
                            for(int e=0;e<=1;e++)
                                for(int o=0;o<=1;o++){
                                    int cnt=f[j][a][b][c][d][e][o];
                                    if(!cnt)continue;
                                    if(o||(j&&dp[a][b][c][d]%j==0)) ans+=cnt;
                                }
        return ans;
    }
    int beautifulNumbers(int l,int r){
        dp[0][0][0][0]=1;
        for(int a=0;a<=mx[1];a++)
            for(int b=0;b<=mx[2];b++)
                for(int c=0;c<=mx[3];c++)
                    for(int d=0;d<=mx[4];d++){
                        long long val=1;
                        for(int i=0;i<a;i++) val*=2;
                        for(int i=0;i<b;i++) val*=3;
                        for(int i=0;i<c;i++) val*=5;
                        for(int i=0;i<d;i++) val*=7;
                        dp[a][b][c][d]=val;
                    }
        if(l<1) l=1;
        return solve(r)-solve(l-1);
    }
};

