class Solution:
    def beautifulNumbers(self, l: int, r: int) -> int:

        def helper(n: int) -> int:
            if n == 0:
                return 0

            cur = str(n)
            size = len(cur)

            @cache
            def dfs(pos: int, sum: int, prod: int, isLimit: bool, isChosen: bool) -> int:
                if pos == size:
                    if not isChosen:
                        return 0
                    return 1 if (prod % sum == 0) else 0

                ans = 0
                if not isChosen:
                    ans += dfs(pos + 1, 0, 1, False, False)

                lb = 0 if isChosen else 1
                rb = int(cur[pos]) if isLimit else 9

                for i in range(lb, rb + 1):
                    ans += dfs(pos + 1, sum + i, prod * i, isLimit and i == int(cur[pos]), True)

                return ans

            return dfs(0, 0, 1, True, False)

        return helper(r) - helper(l - 1)

