class Solution:
    def beautifulNumbers(self, l: int, r: int) -> int:
        return self.count(r) - self.count(l - 1)

    def count(self, number):
        @lru_cache(None)
        def dfs(idx, curr_prod, curr_sum, is_limit, is_leading_zero):
            if idx == n:
                if curr_sum and curr_prod % curr_sum == 0:
                    return 1
                return 0

            output = 0
            if not is_limit:
                for num in range(10):
                    if num == 0 and is_leading_zero:
                        output += dfs(idx + 1, curr_prod, curr_sum + num, False, True)
                    else:
                        output += dfs(idx + 1, curr_prod * num, curr_sum + num, False, False)
            else:
                for num in range(digits[idx]):
                    if num == 0 and is_leading_zero:
                        output += dfs(idx + 1, curr_prod, curr_sum + num, False, True)
                    else:
                        output += dfs(idx + 1, curr_prod * num, curr_sum + num, False, False)

                output += dfs(idx + 1, curr_prod * digits[idx], curr_sum + digits[idx], True, False)
            return output

        if number == 0:
            return 0

        digits = [int(digit) for digit in str(number)]
        n = len(digits)

        return dfs(0, 1, 0, True, True)

