import java.util.*;

class Solution {
    private Map<String, Integer> memo;

    public int beautifulNumbers(int l, int r) {
        return countBeautiful(r) - countBeautiful(l - 1);
    }

    private int countBeautiful(int num) {
        if (num == 0) return 0;

        String s = String.valueOf(num);
        memo = new HashMap<>();

        return digitDP(s, 0, 1, 0, 1, true);
    }

    private int digitDP(String s, int pos, int tight, int sum, int product, boolean leadingZero) {
        if (pos == s.length()) {
            return (sum > 0 && product % sum == 0) ? 1 : 0;
        }

        // Memoization key
        String key = pos + "|" + tight + "|" + sum + "|" + product;
        if (memo.containsKey(key)) return memo.get(key);

        int limit = (tight == 1) ? s.charAt(pos) - '0' : 9;
        int res = 0;

        for (int d = 0; d <= limit; d++) {
            res += digitDP(s, pos + 1, (tight == 1 && d == limit) ? 1 : 0,
                           sum + d, (d == 0 && leadingZero) ? 1 : product * d, leadingZero && d == 0);
        }

        memo.put(key, res);
        return res;
    }
}

