class Solution {
public:
    int beautifulNumbers(int l, int r) {
        //* dbg(l, r);

        bool vis[10 + 1][2][2][81 + 1][81 + 1];
        ll  memo[10 + 1][2][2][81 + 1][81 + 1];

        auto work = [&](ll modSum) -> ll {
            auto pref = [&](ll x) -> ll {
                vector<int> digits;
                for(auto& c: ts(x)) 
                    digits.eb(int(c - '0'));
                const int m = sz(digits);

                memset(vis, false, sizeof(vis));
                auto dp = [&](auto&& self, int i, bool lastWasDefined, bool reachedLower, int mod, int sum) -> ll {
                    if(sum > modSum) return 0;
                    if(i == m) {
                        if(!lastWasDefined) return 0;
                        if(sum == modSum && mod == 0) return 1;
                        return 0;
                    }                    

                    if(vis[i][lastWasDefined][reachedLower][mod][sum]) return memo[i][lastWasDefined][reachedLower][mod][sum];
                    vis[i][lastWasDefined][reachedLower][mod][sum] = true;

                    ll ans = 0;
                    if(!lastWasDefined) {
                        ans += self(self, i + 1, lastWasDefined, reachedLower, mod, sum);

                        if(i == 0) {
                            for(int d = 1; d <= digits[i]; d++) {
                                ans += self(self, i + 1, true, d < digits[i], (mod * d) % modSum, sum + d);
                            }
                        } else {
                            for(int d = 1; d <= 9; d++) {
                                ans += self(self, i + 1, true, true, (mod * d) % modSum, sum + d);
                            }
                        }
                    } else {
                        if(reachedLower) {
                            for(int d = 0; d <= 9; d++) {
                                ans += self(self, i + 1, true, true, (mod * d) % modSum, sum + d);
                            }
                        } else {
                            for(int d = 0; d <= digits[i]; d++) {
                                ans += self(self, i + 1, true, d < digits[i], (mod * d) % modSum, sum + d);
                            }
                        }
                    }
                    return memo[i][lastWasDefined][reachedLower][mod][sum] = ans;
                };
                ll res = dp(dp, 0, false, false, 1, 0);
                return res;
            };

            ll left = pref(l - 1);
            ll right = pref(r);

            ll res = right - left;
            return res;
        };

        ll response = 0;
        for(ll sum = 1; sum <= 81; sum++) {
            ll contrib = work(sum);
            response += contrib;
        }

        return response;
    }
};

