class Solution {
public:
    int yh[1000][10];
    int bh[8][5][3][3], shu[1000];
    int fan[1000][4], B;
    int f[2][90][400];

    void calc_bh_yh(){
        int a, b, c, d, e, na, nb, nc, nd, i;
        B = 0;
        for (a = 0; a <= 7; ++a)
            for (b = 0; b <= 4; ++b)
                for (c = 0; c <= 2; ++c)
                    for (d = 0; d <= 2; ++d){
                        if (a == 7 && (b > 0 || c > 0 || d > 0))
                            continue;
                        bh[a][b][c][d] = B;
                        fan[B][0] = a;
                        fan[B][1] = b;
                        fan[B][2] = c;
                        fan[B][3] = d;
                        if (a == 7) shu[B] = 0;
                        else{
                            shu[B] = 1;
                            for (i = 0; i < a; ++i) shu[B] *= 2;
                            for (i = 0; i < b; ++i) shu[B] *= 3;
                            for (i = 0; i < c; ++i) shu[B] *= 5;
                            for (i = 0; i < d; ++i) shu[B] *= 7;
                        }
                        ++B;
                    }

        for (a = 0; a <= 7; ++a)
            for (b = 0; b <= 4; ++b)
                for (c = 0; c <= 2; ++c)
                    for (d = 0; d <= 2; ++d){
                        if (a == 7 && (b > 0 || c > 0 || d > 0))
                            continue;
                        for (e = 0; e < 10; ++e){
                            if (a == 7 || e == 0){
                                yh[bh[a][b][c][d]][e] = bh[7][0][0][0];
                                continue;
                            }
                            na = a; nb = b; nc = c; nd = d;
                            if (e == 2) ++na;
                            if (e == 3) ++nb;
                            if (e == 4) na += 2;
                            if (e == 5) ++nc;
                            if (e == 6){
                                ++na;
                                ++nb;
                            }
                            if (e == 7) ++nd;
                            if (e == 8) na += 3;
                            if (e == 9) nb += 2;
                            na = min(na, 6);
                            nb = min(nb, 4);
                            nc = min(nc, 2);
                            nd = min(nd, 2);
                            yh[bh[a][b][c][d]][e] = bh[na][nb][nc][nd];
                        }
                    }
    }

    int dp(string S){
        int i, j, k, s, r = 0, sum = 0, state = 0, L, old, now, top;
        L = S.size();
        top = L * 9;
        for (int w = 0; w < L; ++w){
            int c = S[w] - '0';
            for (int q = 0; q < c; ++q){
                if (w == 0 && q == 0) continue;
                now = 0;
                for (i = 0; i <= top; ++i)
                    for (j = 0; j < B; ++j)
                        f[now][i][j] = 0;
                f[now][sum + q][yh[state][q]] = 1;
                for (int o = w + 1; o < L; ++o){
                    old = now; now = 1 - now;
                    for (i = 0; i <= top; ++i)
                        for (j = 0; j < B; ++j)
                            f[now][i][j] = 0;
                    for (i = 0; i <= top; ++i)
                        for (j = 0; j < B; ++j)
                            if (f[old][i][j] > 0){
                                for (k = 0; k < 10; ++k)
                                    f[now][i + k][yh[j][k]] += f[old][i][j];
                            }
                }

                for (i = 1; i <= top; ++i)
                    for (j = 0; j < B; ++j)
                        if (shu[j] % i == 0)
                            r += f[now][i][j];
            }

            sum += c;
            state = yh[state][c];
        }

        // n itself
        if (shu[state] % sum == 0) ++r;
        return r;
    }

    int gao(int n){
        if (n == 0) return 0;
        string S = to_string(n);
        int L = S.size(), r = 0, i, j;
        r += dp(S);
        for (i = 1; i < L; ++i){
            S = "";
            for (j = 0; j < i; ++j) S += "9";
            r += dp(S);
        }

        return r;
    }

    int beautifulNumbers(int l, int r) {
        calc_bh_yh();
        // int i, j, q = 0, s, t, x, w = 0;
        // for (i = l; i <= r; ++i){
        //     x = i;
        //     s = 0; t = 1;
        //     while (x){
        //         s += x % 10;
        //         t *= x % 10;
        //         x /= 10;
        //     }
        //     if (t % s == 0) ++w;
        // }
        // cout<<w<<endl;
        return gao(r) - gao(l - 1);
    }
};

