class Solution:
    def beautifulNumbers(self, l: int, r: int) -> int:
        def count_beautiful(X: int) -> int:
            if X < 0:
                return 0
            s = str(X)
            n = len(s)
            from functools import lru_cache

            @lru_cache(maxsize=None)
            def dp(pos, tight, started, prod, summ):
                if pos == n:
                    return 1 if (started and summ > 0 and prod % summ == 0) else 0
                res = 0
                limit = int(s[pos]) if tight else 9
                for d in range(limit + 1):
                    ntight = tight and (d == limit)
                    if not started:
                        if d == 0:
                            res += dp(pos + 1, ntight, False, 1, 0)
                        else:
                            res += dp(pos + 1, ntight, True, d, d)
                    else:
                        new_prod = prod * d if d != 0 else 0
                        res += dp(pos + 1, ntight, True, new_prod, summ + d)
                return res

            return dp(0, True, False, 1, 0)

        return count_beautiful(r) - count_beautiful(l - 1)

