public class Solution {
    public int BeautifulNumbers(int l, int r) {
                return GetValue(r) - GetValue(l - 1);
}

public static int GetValue(int n)
{
    if (n == 0) return 0;
    var digits = GetDigits(n);
    var memo = new Dictionary<(int, bool, int, int, bool), int>();

    return Dp(0, true, 0, 1, false, digits, memo);
}

public static int Dp(int pos, bool tight, int digitSum, int digitProduct, bool hasNonZero, List<int> digits, Dictionary<(int, bool, int, int, bool), int> dp)
{
    if (pos == digits.Count)
    {
        return hasNonZero && digitSum > 0 && digitProduct % digitSum == 0 ? 1 : 0;
    }            

    var key = (pos, tight, digitSum, digitProduct, hasNonZero);
    if (dp.ContainsKey(key))
    {
        return dp[key];
    }            

    int limit = tight ? digits[pos] : 9;
    int total = 0;

    for (int d = 0; d <= limit; d++)
    {
        int newSum = digitSum + d;
        int newProduct = hasNonZero ? digitProduct * d : d;
        total += Dp(pos + 1, tight && (d == limit), newSum, newProduct, hasNonZero || d > 0, digits, dp);
    }

    return dp[key] = total;
}

public static List<int> GetDigits(int n)
{
    var digits = new List<int>();
    while (n > 0)
    {
        digits.Add(n % 10);
        n /= 10;
    }
    digits.Reverse();
    return digits;
}
}

