class Solution:
    def beautifulNumbers(self, l: int, r: int) -> int:
        def count_beautiful_upto(x):
            digits = list(map(int, str(x)))
            n = len(digits)

            @lru_cache(None)
            def recurse(pos, is_tight, started, total, product):
                if pos == n:
                    if not started:
                        return 0

                    return 1 if product % total == 0 else 0

                limit = digits[pos] if is_tight else 9
                current_total = 0

                for d in range(0, limit + 1):
                    new_is_tight = is_tight and (d == limit)

                    if not started:
                        if d == 0:
                            current_total += recurse(pos + 1, new_is_tight, False, 0, 1)
                        else:
                            current_total += recurse(pos + 1, new_is_tight, True, d, d)
                    else:
                        current_total += recurse(pos + 1, new_is_tight, True, total + d, product * d)

                return current_total

            return recurse(0, True, False, 0, 1)

        return count_beautiful_upto(r) - count_beautiful_upto(l - 1)

