            return value == 0 and current == sum;
        if (dp[i][low][high][value][current][zero] != -1)
            return dp[i][low][high][value][current][zero];
        int lower = 0, higher = 9, ans = 0;
        if (low)
            lower = L[i] - '0';
        if (high)
            higher = R[i] - '0';
        for (int d = lower; d <= higher; d++)
        {
            int new_low = low and d == lower ? 1 : 0, new_high = high and d == higher ? 1 : 0, new_zero = zero and !d ? 1 : 0;
            int new_current = zero and d == 0 ? current : current + d;
            if (new_current > sum)
                continue;
            int new_value;
            if (zero and d == 0)
                new_value = value;
            else if (zero and d != 0)
                new_value = d % sum;
            else
                new_value = (value * d) % sum;
            ans += solve(i + 1, n, new_value, new_current, new_low, new_high, new_zero, L, R, sum);
        }
        return dp[i][low][high][value][current][zero] = ans;
    }

public:
    int beautifulNumbers(int l, int r)
    {
        ios::sync_with_stdio(false);
        cin.tie(nullptr);
        cout.tie(nullptr);

        string L = to_string(l),R = to_string(r);
        reverse(L.begin(), L.end());
        while (L.size() < R.size())
            L += '0';
        reverse(L.begin(), L.end());
        int ans = 0, n = L.size();
        for (int sum = 1; sum <= 9 * n; sum++)
        {
            for(int i = 0; i < n; i++)
                for(int j = 0; j < 2; j++)
                    for(int k = 0; k < 2; k++)
                        for(int x = 0; x <= sum; x++)
                            for(int y = 0; y <= sum; y++)
                                for(int z = 0; z < 2; z++)
                                    dp[i][j][k][x][y][z] = -1;
            ans += solve(0, n, 1, 0, 1, 1, 1, L, R, sum);
        }
        return ans;
    }
};

