class Solution:
    def beautifulNumbers(self, l: int, r: int) -> int:
        def f(r):
            if r < 0:
                return 0
            s = str(r)
            @lru_cache(None)
            def go(i, tight, started, su, prod):
                if i == len(s):
                    if not started:
                        return 0
                    return 1 if prod % su == 0 else 0
                limit = int(s[i]) if tight else 9
                ans = 0
                for d in range(limit + 1):
                    ntight = tight and (d == limit)
                    if not started and d == 0:
                        ans += go(i + 1, ntight, False, 0, 1)
                    else:
                        ans += go(i + 1, ntight, True, su + d, prod * d)
                return ans
            result = go(0, True, False, 0, 1)
            go.cache_clear()  
            return result
        return f(r) - f(l - 1)

