using ll = long long;

bool did_precomp = false;

vector<pair<int,array<int,10>>> goods;
int cnt[10];
void rec(int i, int rem) {
    if (i == 10) {
        if (rem != 0) return;
        ll sumd = 0;
        ll prodd = 1;
        for (int i = 0; i < 10; ++i) {
            for (int j = 0; j < cnt[i]; ++j) {
                sumd +=(ll) i;
                prodd *= (ll)i;
            }
        }
        if (sumd != 0 && prodd % sumd == 0) {
            array<int,10> arr;
            int tot = 0;
            for (int i = 0; i < 10; ++i) {
                arr[i] = cnt[i];
                tot += cnt[i];
            }
            goods.push_back({tot,arr});
        }
        return;
    }
    for (int give = 0; give <= rem; ++give) {
        cnt[i] = give;
        rec(i+1, rem-give);
    }
}


int fac[15];

void precomp() {
    if (did_precomp) return;
    did_precomp = true;
    for (int len = 1; len <= 9; ++len) {
        memset(cnt, 0, sizeof cnt);
        rec(0, len);
    }
    fac[0] = 1;
    for (int i = 1; i < 10; ++i) fac[i] = fac[i-1] * i;
}



int perm(const array<int,10>& a) {
    int tot = 0;
    for (int n : a) tot += n;
    int ret = fac[tot];
    for (int n : a) ret /= fac[n];
    return ret;
}

int f(int n) {
    int ans = 0;
    string s = to_string(n);
    int N = s.size();
    for (auto [len, cnts] : goods) {
        if (len < N) {
            for (int d = 1; d < 10; ++d) {
                if (cnts[d]) {
                    --cnts[d];
                    ans += perm(cnts);
                    ++cnts[d];
                }
            }
        } else if (len > N) {

        } else {
            auto rec = [&](auto&& self, int i) -> void {
                if (i == N) {
                    ++ans;
                    return;
                }
                int start = 0;
                if (i == 0) start = 1;
                for (int d = start; d < s[i]-'0'; ++d) {
                    if (cnts[d]) {
                        --cnts[d];
                        ans += perm(cnts);
                        ++cnts[d];
                    }
                }
                int d = s[i]-'0';
                if (cnts[d]) {
                    --cnts[d];
                    self(self, i+1);
                    ++cnts[d];
                }
            };
            rec(rec, 0);
        }
    }


    return ans;
}


int slow(int n, bool print =false) {
    int ans =0;
    for (int i = 0; i <= n; ++i) {
        string s = to_string(i);
        int sumd = 0, prodd = 1;
        for (char c : s) {
            int d = c-'0';
            sumd += d;
            prodd *= d;
        }
        if (sumd > 0 && prodd % sumd == 0) {
            ++ans;
            if (print) cout << "good: " << s << 'n';
        }
    }
    return ans;

}

class Solution {
public:
    int beautifulNumbers(int l, int r) {
        precomp();
        /*slow(105, true);
        for (int i = 0; i < 1000; ++i) {
            if (f(i) != slow(i)) {
                cout << i << ' ' << f(i) << ' ' << slow(i) << 'n';
            }
        }*/
        return f(r)-f(l-1);
    }
};

