#include <bits/stdc++.h>
using namespace std;

struct Node {
    int idx, lim, flag, zero, s, p;
    bool operator==(const Node &o) const {
        return idx == o.idx && lim == o.lim && flag == o.flag && zero == o.zero && s == o.s && p == o.p;
    }
};

struct NodeHash {
    size_t operator()(const Node &n) const {
        size_t a = hash<int>()(n.idx);
        size_t b = hash<int>()(n.lim);
        size_t c = hash<int>()(n.flag);
        size_t d = hash<int>()(n.zero);
        size_t e = hash<int>()(n.s);
        size_t f = hash<int>()(n.p);
        return (((((a * 31 + b) * 31 + c) * 31 + d) * 31 + e) * 31 + f);
    }
};

class Solution {
public:
    using ll = long long;
    unordered_map<Node, ll, NodeHash> cache;

    ll rec(string &num, int idx, int lim, int flag, int zero, int s, int p) {
        if (idx == num.size()) {
            if (!flag) return 0;
            if (zero) return 1;
            return (p % s == 0) ? 1 : 0;
        }
        Node node {idx, lim, flag, zero, s, p};
        if(cache.count(node)) return cache[node];
        int hi = (lim ? (num[idx]-'0') : 9);
        ll ret = 0;
        for (int d = 0; d <= hi; d++){
            int nlim = lim && (d == hi);
            int nflag = flag;
            int nzero = zero;
            int ns = s;
            int np = p;
            if (!flag && d == 0){
                ret += rec(num, idx+1, nlim, 0, 0, 0, 1);
            } else {
                nflag = 1;
                if (d == 0){
                    nzero = 1;
                    np = 0;
                    ns += 0;
                } else {
                    if (!nzero){
                        ns += d;
                        np *= d;
                    }
                }
                ret += rec(num, idx+1, nlim, nflag, nzero, ns, np);
            }
        }
        cache[node] = ret;
        return ret;
    }

    long long countUpTo(int n) {
        string s = to_string(n);
        cache.clear();
        return rec(s, 0, 1, 0, 0, 0, 1);
    }

    int beautifulNumbers(int l, int r) {
        return (int)(countUpTo(r) - countUpTo(l-1));
    }
};

