import java.util.HashMap;

class Solution {
    private HashMap<String, Integer> memo;

    public int beautifulNumbers(int l, int r) {
        return countBeautifulNumbers(r) - countBeautifulNumbers(l - 1);
    }

    private int countBeautifulNumbers(int num) {
        char[] digits = String.valueOf(num).toCharArray();
        memo = new HashMap<>();
        return digitDP(0, 1, 0, 1, true, digits);
    }

    private int digitDP(int pos, int tight, int sum, int product, boolean isLeadingZero, char[] digits) {
        if (pos == digits.length)
            return sum > 0 && product % sum == 0 ? 1 : 0;

        String key = pos + "," + tight + "," + sum + "," + product + "," + isLeadingZero;
        if (memo.containsKey(key))
            return memo.get(key);

        int limit = tight == 1 ? digits[pos] - '0' : 9;
        int result = 0;

        for (int d = 0; d <= limit; d++) {
            result += digitDP(
                pos + 1, 
                (tight == 1 && d == limit) ? 1 : 0, 
                sum + d, 
                (isLeadingZero && d == 0) ? 1 : product * d, 
                isLeadingZero && d == 0, 
                digits
            );
        }

        memo.put(key, result);
        return result;
    }
}

