class Solution:
    def beautifulNumbers(self, l: int, r: int) -> int:
        range_input = [l, r]
        count_r = self.count_up_to(r)
        count_l_minus = self.count_up_to(l - 1)
        return int(count_r - count_l_minus)

    def count_up_to(self, n: int) -> int:
        if n < 1:
            return 0
        digits = list(str(n))
        memo = {}
        return self.dp(0, True, False, 0, 1, digits, memo)

    def dp(self, pos: int, tight: bool, started: bool, current_sum: int, current_prod: int, digits: list, memo: dict) -> int:
        if pos == len(digits):
            if started and current_sum > 0 and current_prod % current_sum == 0:
                return 1
            return 0

        key = f"{pos},{1 if tight else 0},{1 if started else 0},{current_sum},{current_prod}"
        if key in memo:
            return memo[key]

        limit = int(digits[pos]) if tight else 9
        result = 0
        for d in range(0, limit + 1):
            next_tight = tight and (d == limit)
            new_started = started or (d != 0)
            new_sum = current_sum
            new_prod = current_prod
            if new_started:
                if not started:
                    new_sum = d
                    new_prod = d
                else:
                    new_sum = current_sum + d
                    new_prod = current_prod * d
            result += self.dp(pos + 1, next_tight, new_started, new_sum, new_prod, digits, memo)
        memo[key] = result
        return result

