            digits = list(map(int, str(n)))
            length = len(digits)

            @lru_cache(None)
            def dp(pos, tight, digit_sum, digit_prod, leading_zero):
                if pos == length:
                    return 1 if digit_sum > 0 and digit_prod % digit_sum == 0 else 0

                limit = digits[pos] if tight else 9
                count = 0

                for d in range(0, limit + 1):
                    new_sum = digit_sum + d
                    new_prod = digit_prod if d == 0 and leading_zero else digit_prod * d
                    count += dp(pos + 1, tight and (d == limit), new_sum, new_prod, leading_zero and d == 0)

                return count

            return dp(0, True, 0, 1, True)

        return count_beautiful(r) - count_beautiful(l - 1)

