class Solution {
public:
    int beautifulNumbers(int l, int r) {
        return calc(r+1) - calc(l);
    }
    int calc(int n) {
        int x = n;
        vector<int> digits;
        while (x > 0) {
            digits.push_back(x % 10);
            x /= 10;
        }
        reverse(digits.begin(), digits.end());

        int cnt = 0;
        memo.clear();

        // num digits < n
        for (int nd = digits.size()-1; nd >= 1; nd--) {
            for (int i = 1; i <= 9; i++) {
                cnt += backtrack(i, i, nd-1);
            }
        }

        // matching first few digits
        for (int match = 0; match < digits.size(); match++) {
            int sum = 0, product = 1;
            for (int i = 0; i < match; i++) {
                sum += digits[i];
                product *= digits[i];
            }
            for (int i = 0; i < digits[match]; i++) {
                if (match == 0 && i == 0) continue;
                cnt += backtrack(product * i, sum + i, digits.size() - match - 1);
            }
        }

        return cnt;
    }

    int backtrack(int product, int sum, int ndigits) {
        long long hash = 1000LL * product + 10LL * sum + ndigits;
        if (memo.count(hash)) {
            return memo[hash];
        }
        // printf("backtrack(product=%d, sum=%d, nd=%d)n", product, sum, ndigits);
        if (ndigits == 0) {
            if (product % sum == 0) {
                return memo[hash] = 1;
            }
            return memo[hash] = 0;
        }
        if (product == 0) {
            int p = 1;
            for (int i = 0; i < ndigits; i++) {
                p *= 10;
            }
            return memo[hash] = p;
        }
        int cnt = 0;
        for (int i = 0; i <= 9; i++) {
            cnt += backtrack(product * i, sum + i, ndigits-1);
        }
        return memo[hash] = cnt;
    }

    map<long long,int> memo;
};

