from functools import cache
class Solution:
    def beautifulNumbers(self, l: int, r: int) -> int:
        nums = [[int(x) for x in str(l - 1)], [int(x) for x in str(r)]]
        @cache
        def solve(i, sums, prod, upper, edge, all_zero):
            if i < 0: return int(prod % sums == 0) if sums != 0 else 0
            res = 0
            digit = nums[upper][-i - 1]
            if all_zero:
                res += solve(i - 1, sums, prod, upper, edge and digit == 0, True)
            if edge:
                if not (all_zero and digit == 0):
                    res += solve(i - 1, sums + digit, prod * digit, upper, True, False)
                for j in range(int(all_zero), digit):
                    res += solve(i - 1, sums + j, prod * j, upper, False, False)
            else:
                for j in range(int(all_zero), 10):
                    res += solve(i - 1, sums + j, prod * j, upper, False, False)
            return res
        def get(num): return solve(len(nums[num]) - 1, 0, 1, num, True, True)
        # print(get(1), get(0))
        return get(1) - get(0)
