class Solution:
    def beautifulNumbers(self, l: int, r: int) -> int:

        def resolve(x):
            num = str(x)
            @cache
            def dfs(i: int, isLimit: bool, isNum: bool, p, s) -> int:
                if i == len(num):
                    return isNum and p % s == 0

                ans = 0
                if not isNum:
                    ans = dfs(i+1, False, False, 1, 0)

                low = 0 if isNum else 1
                high = int(num[i]) if isLimit else 9
                for d in range(low, high+1):
                    ans += dfs(i+1, isLimit and d == high, True, p * d, s + d)
                return ans

            return dfs(0, True, False, 1, 0)

        return resolve(r) - resolve(l-1)

