from functools import lru_cache

class Solution:
    def beautifulNumbers(self, l: int, r: int) -> int:
        return self.countBeautiful(r) - self.countBeautiful(l - 1)

    def countBeautiful(self, num: int) -> int:
        if num == 0:
            return 0

        digits = list(map(int, str(num))) 

        @lru_cache(None)
        def digitDP(pos: int, sum_: int, product: int, tight: bool, leading_zero: bool) -> int:
            if pos == len(digits):
                return 1 if sum_ > 0 and product % sum_ == 0 else 0

            limit = digits[pos] if tight else 9
            result = 0

            for d in range(0, limit + 1):
                new_leading_zero = leading_zero and (d == 0)
                new_sum = sum_ if new_leading_zero else sum_ + d
                new_product = product if new_leading_zero else product * d

                result += digitDP(
                    pos + 1,
                    new_sum,
                    new_product,
                    tight and (d == limit),
                    new_leading_zero
                )

            return result

        return digitDP(0, 0, 1, True, True)

