class Solution {
public:
    unordered_map<int, unordered_map<int, unordered_map<int, int>>> dp;
    vector<int> digits;

    //  DP
    int dfs(int pos, int sum_digits, int prod_digits, bool limit, bool leading_zero) {
        if (pos == digits.size()) 
            return sum_digits > 0 && prod_digits % sum_digits == 0;

        if (!limit && !leading_zero && dp[pos][sum_digits].count(prod_digits)) 
            return dp[pos][sum_digits][prod_digits];

        int upper = limit ? digits[pos] : 9;
        int res = 0;

        for (int d = 0; d <= upper; ++d) {
            res += dfs(pos + 1, 
                       sum_digits + d, 
                       leading_zero && d == 0 ? 1 : prod_digits * d,  //  0 
                       limit && (d == upper), 
                       leading_zero && (d == 0));
        }

        if (!limit && !leading_zero) 
            dp[pos][sum_digits][prod_digits] = res;

        return res;
    }

    //  [1, x]  beautiful numbers 
    int countBeautiful(int x) {
        if (x == 0) return 0;

        digits.clear();
        while (x) {
            digits.push_back(x % 10);
            x /= 10;
        }
        reverse(digits.begin(), digits.end());

        dp.clear();  //  unordered_map
        return dfs(0, 0, 1, true, true);
    }

    //  [l, r]  beautiful numbers 
    int beautifulNumbers(int l, int r) {
        return countBeautiful(r) - countBeautiful(l - 1);
    }
};

