        int res = 0;
        string left = to_string(l);
        string right = to_string(r);
        left = string(right.size() - left.size(), '0') + left;
        int dp[right.size()][82][82][2][2][2];
        for (int i = 0; i < right.size(); i++) {
            for (int j = 0; j < 82; j++) {
                for (int k = 0; k < 82; k++) {
                    for (int a = 0; a < 2; a++) {
                        for (int b = 0; b < 2; b++) {
                            for (int c = 0; c < 2; c++) {
                                dp[i][j][k][a][b][c] = -1;
                            }
                        }
                    }
                }
            }
        }
        for (int sum = 1; sum <= 81; sum++) {
            // cout << f(left, right, 0, sum, sum, 0, 0, 0, dp) << endl;
            res += f(left, right, 0, sum, sum, 0, 0, 0, dp);
        }
        return res;
    }

private:
    int f(string& l, string& r, int i, int a, int b, int x, int y, int z, int dp[][82][82][2][2][2]) {
        if (i == r.size()) {
            // cout  << i << " "<< a << " " << b << " " << x << endl;
            return a == 0 && b == 1 ? 1 : 0;
        }
        // cout << i << " " << a << " " << b << " " << x << " " << y
        if (dp[i][a][b][x][y][z] != -1) {
            return dp[i][a][b][x][y][z];
        }

        // x=1, cur > l; y == 1, cur < r; z = 1; cur != 0
        // int res = x == 1 && a == 0 && b == 1 ? 1 : 0;
        int res = 0;
        int cl = l[i] - '0', cr = r[i] - '0';
        for (int c = 0; c < 10 && c <= a; c++) {
            if (x == 0 && c < cl) continue;
            if (y == 0 && c > cr) break;
            int nb = b;
            if (z == 1 || c > 0) {
                if (c == 0) nb = 1;
                else nb = b / gcd(b, c);
            } 
            res += f(l, r, i + 1, a - c, nb, x || c > cl, y || c < cr, z || c > 0, dp);
        }
        // cout << i << " " << a << " " << b << " " << x << " " << y << " " <<z << " " << res << endl;
        return dp[i][a][b][x][y][z] = res;
    }
};

