int dp[2][100][100];
int ndp[2][100][100];

class Solution {
public:
  int beautifulNumbers(int l, int r) {
    auto Get = [&](int x) -> int {
      if (x == 0) {
        return 0;
      }
      vector<int> nums;
      do {
        nums.emplace_back(x % 10);
        x /= 10;
      } while (x > 0);
      reverse(nums.begin(), nums.end());
      int n = (int) nums.size();
      int sum = accumulate(nums.begin(), nums.end(), 0);
      int res = 0;
      for (int sum = 1; sum <= 9 * n; sum++) {
        memset(dp, 0, sizeof dp);
        for (int i = 0; i < n; i++) {
          memset(ndp, 0, sizeof ndp);
          for (int c = 1; c <= (i == 0 ? nums[i] : 9); c++) {
            if (c <= sum) {
              ndp[i == 0 && c == nums[i]][c % sum][c] += 1;
            }
          }
          for (int limit = 0; limit <= 1; limit++) {
            for (int s = 0; s < sum; s++) {
              for (int cur = 0; cur <= 9 * n; cur++) {
                for (int c = 0; c <= (limit ? nums[i] : 9); c++) {
                  if (cur + c <= sum) {
                    ndp[limit && c == nums[i]][(s * c) % sum][cur + c] += dp[limit][s][cur];
                  }
                }
              }
            }
          }
          memcpy(dp, ndp, sizeof ndp);
        }
        for (int limit = 0; limit <= 1; limit++) {
          res += dp[limit][0][sum];
        }
      }
      return res;
    };
    return Get(r) - Get(l - 1);
  }
};

