class Solution:
    def beautifulNumbers(self, l: int, r: int) -> int:
        def cal(s: str) -> int:

            @cache
            def dfs(i: int, is_limit: bool, is_num: bool, sum: int, p: int) -> int:
                if i == len(s):
                    if is_num:
                        return 1 if (p == 0 or (sum != 0 and p % sum == 0)) else 0
                    return 0

                res = 0
                if not is_num:
                    res += dfs(i + 1, False, False, 0, 1)

                down = 0 if is_num else 1
                up = int(s[i]) if is_limit else 9
                for u in range(down, up + 1):
                    nl = is_limit and (u == int(s[i]))
                    nf = True
                    ns = np = 0
                    if not is_num:
                        ns = np = u
                    else:
                        ns = sum + u
                        np = 0 if (p == 0 or u == 0) else p * u
                    res += dfs(i + 1, nl, nf, ns, np)
                return res

            return dfs(0, True, False, 0, 1)

        return cal(str(r)) - cal(str(int(l) - 1))

