class Solution {
    Map<String, Integer> dp = new HashMap<>();
    public int beautifulNumbers(int l, int r) {
        dp = new HashMap<>();
        int[] nums1 = numToArr(r);
        int[] nums2 = numToArr(l - 1);
        int a = dfs(nums1, 0, 0, 1, true, false);
        dp = new HashMap<>();
        int b = l == 1 ? 0 : dfs(nums2, 0, 0, 1, true, false);

        return a - b;
    }

    int[] numToArr(int num) {
        int len = 0;
        int t = num;
        while (t != 0)    {
            len++;
            t /= 10;
        }
        int[] nums = new int[len];
        for (int i = len - 1; i >= 0; i--)  {
            nums[i] = num % 10;
            num /= 10;
        }
        return nums;
    }

    int dfs(int[] nums, int idx, int sum, int prod, boolean isLimit, boolean isNum)  {
        if (idx >= nums.length)
            return isNum && prod % sum == 0 ? 1 : 0;
        String key = idx + "_" + sum + "_" + prod;
        if (dp.containsKey(key) && !isLimit && isNum)
            return dp.get(key);

        int ans = 0;

        if (!isNum) 
            ans = dfs(nums, idx + 1, 0, 1, false, false);

        int lo = isNum ? 0 : 1;
        int hi = isLimit ? nums[idx] : 9;

        for (int i = lo; i <= hi; i++)  {
            ans += dfs(nums, idx + 1, sum + i, prod * i, isLimit && nums[idx] == i, true);
        }

        if (!isLimit && isNum)
            dp.put(key, ans);

        return ans;
    }


}

