class Solution {

    private static class Element {
        public int cur;          // 10
        public int less;         // 2
        public int sum;          // 81
        public int productMod;   // 81
        public Element(int cur, int less, int sum, int productMod) {
            this.cur = cur;
            this.less = less;
            this.sum = sum;
            this.productMod = productMod;
        }
        @Override
        public int hashCode() {
            int ans = 31;
            ans = ans * 37 + this.cur;
            ans = ans * 37 + this.less;
            ans = ans * 37 + this.sum;
            ans = ans * 37 + this.productMod;
            return ans;
        }
        @Override
        public boolean equals(Object obj) {
            Element e = (Element) obj;
            return this.cur == e.cur && this.less == e.less && this.sum == e.sum && this.productMod == e.productMod;
        }
    }

    private Map<Element, Integer> dp = new HashMap<>();

    public int beautifulNumbers(int l, int r) {
        return getResult(r) - getResult(l - 1);
    }

    private int getResult(int upper) {
        String number = Integer.toString(upper);
        int ans = 0, len = number.length();
        for (int i = 1;i <= len * 9;i ++) {
            dp.clear();
            ans += solve(number, 0, 0, 0, i, 1);
        }
        return ans;
    }

    private int solve(String number, int cur, int less, int sum, int targetSum, int productMod) {
        if (sum > targetSum) {
            return 0;
        }
        if (cur == number.length()) {
            if (sum == targetSum && productMod == 0) {
                return 1;
            }
            return 0;
        }
        Element e = new Element(cur, less, sum, productMod);
        if (dp.containsKey(e)) {
            return dp.get(e);
        }
        int ans = 0;
        int start = 0;
        int curDigit = number.charAt(cur) - '0', upper = curDigit;
        if (less > 0) {
            upper = 9;
        }
        // iter current digit
        for (int i = start;i <= upper;i ++) {
            int newSum = sum + i;
            int newProdutMod = 1;
            if (newSum > 0) {
                newProdutMod = (productMod * i) % targetSum;
            }
            int newLess = 0;
            if (less > 0) {
                newLess = 1;
            } else {
                if (i < curDigit) {
                    newLess = 1;
                }
            }
            ans += solve(number, cur + 1, newLess, newSum, targetSum, newProdutMod);
        }
        dp.put(e, ans);
        return ans;
    }

}

