    def count_beautiful_numbers(self, l, r):
        def solve(n):
            digits = list(map(int, str(n)))

            @lru_cache(maxsize=None)
            def dp(pos, sum_digits, prod_digits, tight, leading_zero):
                if pos == len(digits):
                    if leading_zero:
                        return 0
                    if prod_digits == 0:
                        return 1
                    return int(prod_digits % sum_digits == 0)

                limit = digits[pos] if tight else 9
                total = 0

                for d in range(0, limit + 1):
                    next_sum = sum_digits + d
                    next_prod = prod_digits * d if not leading_zero or d > 0 else prod_digits
                    next_tight = tight and (d == limit)
                    next_leading_zero = leading_zero and d == 0

                    total += dp(pos + 1, next_sum, next_prod, next_tight, next_leading_zero)
                return total

            return dp(0, 0, 1, True, True)

        return solve(r) - solve(l - 1)

