using ll = long long;
using State = tuple<int, int, ll, bool, bool>;

class Solution {    
public:
    map<State, ll> memo;
    vector<int> digits;

    ll countBeautiful(int pos, int sum, int product, bool tight, bool leading_zero) 
    {
        if (pos == digits.size()) {
            return (sum > 0 && product % sum == 0) ? 1 : 0;
        }

        State key = {pos, sum, product, tight, leading_zero};
        if (memo.find(key) != memo.end()) return memo[key];

        int limit = (tight ? digits[pos] : 9);
        ll res = 0;

        for (int d = 0; d <= limit; d++) {
            res += countBeautiful(pos + 1, sum + d, (leading_zero && d == 0) ? 1 : product * d, tight && (d == limit), leading_zero && (d == 0));
        }

        return memo[key] = res;
    }

    ll helper(ll T) 
    {
        if (T <= 0) return 0;
        digits.clear();
        memo.clear();

        while (T > 0) 
        {
            digits.push_back(T % 10);
            T /= 10;
        }
        reverse(digits.begin(), digits.end());
        return countBeautiful(0, 0, 1, true, true);
    }

    int beautifulNumbers(int l, int r) 
    {
        return helper(r) - helper(l-1);
    }
};

